  //                   "text": "Neraka"
  //               }
  //           ],
  //           "question": "Kemana Anda ingin pergi berlibur?"
  //       },
  //       "5643172898144256": {
  //           "choice": [
  //               {
  //                   "id": 1,
  //                   "reason": "text and <html/>",
  //                   "result": 2,
  //                   "sources": [
  //                       "http://",
  //                       "http://"
  //                   ],
  //                   "text": "Ya"
  //               },
  //               {
  //                   "id": 2,
  //                   "reason": "text and <html/>",
  //                   "result": 1,
  //                   "sources": [
  //                       "http://",
  //                       "http://"
  //                   ],
  //                   "text": "Tidak"
  //               }
  //           ],
  //           "question": "Apakah Anda setuju anggaran militer perlu dinaikkan?"
  //       }
  //   },
  //   "stance": {
  //       "2": 1
  //   },
  //   "status": "ok"
  // }

  constructor (props) {
    super(props);
    this.state = {
        ...this.props,
        current_question: 1,
        questions: {
          1: {
              id: 5633226290757632,
              choice: [
                {
                    id: 1,
                    reason: "text and <html/>",
                    result: 1,
                    sources: [
                        "http://",
                        "http://"
                    ],
                    text: "Pantai"
                },
                {
                    id: 2,
                    reason: "text and <html/>",
                    result: 2,
                    sources: [
                        "http://",
                        "http://"
                    ],
                    text: "Gunung"
                },
                {
                    id: 3,
                    reason: "text and <html/>",
                    result: 0,
                    sources: [
                        "http://",
                        "http://"
                    ],
                    text: "Neraka"
                }
              ],
              question: "Kemana Anda ingin pergi berlibur?"
          },
          2: {
            id: 5643172898144256,
              choice: [
                  {
                      id: 1,
                      reason: "text and <html/>",
                      result: 2,
                      sources: [
                          "http://",
                          "http://"
                      ],
                      text: "Ya"
                  },
                  {
                      id: 2,
                      reason: "text and <html/>",
                      result: 1,
                      sources: [
                          "http://",
                          "http://"
                      ],
                      text: "Tidak"
                  }
              ],
              question: "Apakah Anda setuju anggaran militer perlu dinaikkan?"
          }
        }
    };
  }

  componentDidMount() {}

  currentQuestion() {
    return this.state.questions[this.state.current_question];
  }

  failure() {
      return('Invalid input')
  }

  success() {
    return (
        <div>
            <div>Here goes the question number { this.state.q }, I guess?</div>
            <div>User's Answer</div>
            <div>Unselected Answer</div>
            <div>Unselected Answer</div>
            <div>Result</div>

            <div>Descriptive reasoning...</div>

            <Link href='answer' as={ "/answer/" + this.state.next }>
            <button>Next</button>
            </Link>
        </div>
    )
  }

  render() {
      return(this.success())
  }
}
