import React, { Component } from 'react'
import PropTypes from 'prop-types'
import Link from 'next/link'

export default class extends Component {
  static propTypes = {
    q: PropTypes.number
  }

//   {
//     "answers": {
//         "5633226290757632": {
//             "answer": 0,
//             "id": null
//         },
//         "5643172898144256": {
//             "answer": 1,
//             "id": null
//         }
//     },
//     "last_unanswered": null,
//     "questions": {
//         "5633226290757632": {
//             "choice": [
//                 {
//                     "id": 1,
//                     "text": "Pantai"
//                 },
//                 {
//                     "id": 2,
//                     "text": "Gunung"
//                 },
//                 {
//                     "id": 3,
//                     "text": "Neraka"
//                 }
//             ],
//             "question": "Kemana Anda ingin pergi berlibur?"
//         },
//         "5643172898144256": {
//             "choice": [
//                 {
//                     "id": 1,
//                     "text": "Ya"
//                 },
//                 {
//                     "id": 2,
//                     "text": "Tidak"
//                 }
//             ],
//             "question": "Apakah Anda setuju anggaran militer perlu dinaikkan?"
//         }
//     },
//     "status": "ok"
//  }

  constructor (props) {
    super(props);
    this.state = {
        ...this.props,
        next: this.props.q + 1,        
        answers: {},
        last_unanswered: 1,
        questions: {
          1: {
            id: 5633226290757632,
            choice: [
              {
                id: 1,
                text: "Pantai"
              },
              {
                id: 2,
                text: "Gunung"
              },
              {
                id: 3,
                text: "Neraka"
              }
            ],
            question: "Kemana Anda ingin pergi berlibur?",
            helper: "Pilih salah satu yang paling merepresentasikan Anda."
          },
          2: {
            id: 5643172898144256,
            choice: [
              {
                id: 1,
                text: "Ya"
              },
              {
                id: 2,
                text: "Tidak"
              }
            ],
            question: "Anggaran militer perlu dinaikkan",
            helper: "Setujukah Anda dengan pernyataan tersebut?"
          }
        }
    };
  }

  currentQuestion() {
    return this.state.questions[this.state.last_unanswered];
  }

  hasPrevQuestion() {
    const last_unanswered = this.state.last_unanswered;
    return last_unanswered - 1 > 0
  }

  prevQuestion() {
    const last_unanswered = this.state.last_unanswered;

    if (!this.state.questions.hasOwnProperty(last_unanswered - 1)) {

    } else {
      this.setState({
        last_unanswered: last_unanswered - 1
      });
    }
  }

  nextQuestion() {
    const last_unanswered = this.state.last_unanswered;

    if (!this.state.questions.hasOwnProperty(last_unanswered + 1)) {

    } else {
      // Send the answer
      this.setState({
        last_unanswered: last_unanswered + 1
      });
    }
  }

  currentAnswer() {
    return this.state.answers[this.state.last_unanswered];
  }

  hasAnswer() {
    return typeof this.currentAnswer() !== 'undefined';
  }

  isSelectedAnswer(id) {
    const answer = this.currentAnswer();
    if (typeof answer !== 'undefined' && answer.answer === id) {
      return true;
    }
    return false;
  }

  selectAnswer(e) {
    const answer = {
      answer: parseInt(e.currentTarget.id)
    };

    let answers = this.state.answers;
    answers[this.state.last_unanswered] = answer;
    this.setState(answers);
  }

  componentDidMount() {}

  failure() {
      return('Invalid input')
  }

  success() {
    return (
      <div className="text-center">
        <div className="row">
          <div className="col">
            <h3>{ this.state.last_unanswered }</h3>
            <h2>{ this.currentQuestion().question }</h2>
            <p>{ this.currentQuestion().helper }</p>
            <br/>
          </div>
        </div>
        <div className="row text-left">
          { this.currentQuestion().choice.map((choice, index) => {
            return(
              <div key={ choice.id } className="col">
                <div className="custom-control custom-radio box-radio">
                  <input
                    type="radio"
                    id={ choice.id }
                    name="answer"
                    className="custom-control-input box-control-input"
                    onChange={ (e) => this.selectAnswer(e) }
                    checked={ this.isSelectedAnswer(choice.id) }/>
                  <label
                    className="custom-control-label box-control-label"
                    htmlFor={ choice.id }>
                    { choice.text }
                  </label>
                </div>
              </div>
            )
          }) }
        </div>
        <br/>
        <div className="row">
          <div className="col">
            <button
              type="button"
              className="btn btn-danger btn-block"
              disabled={ !this.hasPrevQuestion() }
              onClick={() => this.prevQuestion()}>
              <b>SEBELUMNYA</b>
            </button>
          </div>
          <div className="col">
          </div>
          <div className="col">
            <button
              type="button"
              className="btn btn-danger btn-block"
              disabled={ !this.hasAnswer() }
              onClick={() => this.nextQuestion()}>
              <b>BERIKUTNYA</b>
            </button>
          </div>
        </div>
      </div>
    )
  }

  render() {
      return(this.success())
  }
}
