import React, { Component } from 'react'
import PropTypes from 'prop-types'
import Link from 'next/link'

export default class extends Component {
  static propTypes = {
    q: PropTypes.number
  }

//   {
//     "answers": {
//         "5633226290757632": {
//             "answer": 0,
//             "id": null
//         },
//         "5643172898144256": {
//             "answer": 1,
//             "id": null
//         }
//     },
//     "last_unanswered": null,
//     "questions": {
//         "5633226290757632": {
//             "choice": [
//                 {
//                     "id": 1,
//                     "text": "Pantai"
//                 },
//                 {
//                     "id": 2,
//                     "text": "Gunung"
//                 },
//                 {
//                     "id": 3,
//                     "text": "Neraka"
//                 }
//             ],
//             "question": "Kemana Anda ingin pergi berlibur?"
//         },
//         "5643172898144256": {
//             "choice": [
//                 {
//                     "id": 1,
//                     "text": "Ya"
//                 },
//                 {
//                     "id": 2,
//                     "text": "Tidak"
//                 }
//             ],
//             "question": "Apakah Anda setuju anggaran militer perlu dinaikkan?"
//         }
//     },
//     "status": "ok"
//  }

  constructor (props) {
    super(props);
    this.state = {
        ...this.props,
        next: this.props.q + 1,        
        answers: {},
        last_unanswered: 5633226290757632,
        questions: {
          5633226290757632: {
            number: 1,
            choice: [
              {
                id: 1,
                text: "Pantai"
              },
              {
                id: 2,
                text: "Gunung"
              },
              {
                id: 3,
                text: "Neraka"
              }
            ],
            question: "Kemana Anda ingin pergi berlibur?",
            helper: "Pilih salah satu yang paling merepresentasikan Anda."
          },
          5643172898144256: {
            number: 2,
            choice: [
              {
                id: 1,
                text: "Ya"
              },
              {
                id: 2,
                text: "Tidak"
              }
            ],
            question: "Anggaran militer perlu dinaikkan",
            helper: "Setujukah Anda dengan pernyataan tersebut?"
          }
        }
    };
  }

  currentQuestion() {
    return this.state.questions[this.state.last_unanswered];
  }

  componentDidMount() {}

  failure() {
      return('Invalid input')
  }

  success() {
    return (
      <div class="text-center">
        <div class="row">
          <div class="col">
            <h3>{ this.currentQuestion().number }</h3>
            <h2>{ this.currentQuestion().question }</h2>
            <p>{ this.currentQuestion().helper }</p>
            <br/>
          </div>
        </div>
        <div class="row text-left">
          { this.currentQuestion().choice.map((choice, index) => {
            return("a")
          }) }
          <div class="col">
            <div class="custom-control custom-radio box-radio">
              <input type="radio" id="customRadio1" name="customRadio" class="custom-control-input box-control-input"/>
              <label class="custom-control-label box-control-label" for="customRadio1">Setuju</label>
            </div>
          </div>
          <div class="col">
            <div class="custom-control custom-radio box-radio">
              <input type="radio" id="customRadio2" name="customRadio" class="custom-control-input box-control-input"/>
              <label class="custom-control-label box-control-label" for="customRadio2">Tidak Setuju, karena ini contoh kalimat panjang. Akan tetapi jawaban yang satunya singkat padat dan jelas. Tidak seperti yang ini.</label>
            </div>
          </div>
          <div class="col">
            <div class="custom-control custom-radio box-radio">
              <input type="radio" id="customRadio3" name="customRadio" class="custom-control-input box-control-input"/>
              <label class="custom-control-label box-control-label" for="customRadio3">Tidak Setuju, karena ini contoh kalimat panjang. Akan tetapi jawaban yang satunya singkat padat dan jelas. Tidak seperti yang ini.</label>
            </div>
          </div>
          <div class="col">
            <div class="custom-control custom-radio box-radio">
              <input type="radio" id="customRadio4" name="customRadio" class="custom-control-input box-control-input"/>
              <label class="custom-control-label box-control-label" for="customRadio4">Tidak Setuju, karena ini contoh kalimat panjang. Akan tetapi jawaban yang satunya singkat padat dan jelas. Tidak seperti yang ini.</label>
            </div>
          </div>
        </div>
        <br/>
        <div class="row">
          <div class="col">
            <Link href='question' as={ "/question/" + this.state.next }>
              <button type="button" class="btn btn-danger btn-block"><b>SEBELUMNYA</b></button>
            </Link>
          </div>
          <div class="col">
          </div>
          <div class="col">
            <Link href='question' as={ "/question/" + this.state.next }>
              <button type="button" class="btn btn-danger btn-block"><b>BERIKUTNYA</b></button>
            </Link>
          </div>
        </div>
      </div>
    )
  }

  render() {
      return(this.success())
  }
}
