import React, { Component } from 'react'
import PropTypes from 'prop-types'
import Link from 'next/link'

export default class extends Component {
  static propTypes = {
    q: PropTypes.number
  }

  constructor (props) {
    super(props);
    this.state = {
        ...this.props,
        next: this.props.q + 1
    };
  }

  componentDidMount() {}

  failure() {
      return('Invalid input')
  }

  success() {
    return (
      <div class="text-center">
        <div class="row">
          <div class="col">
            <h3>{ this.state.q }</h3>
            <h2>Peningkatan gaji pejabat, birokrat, dan semua PNS secara signifikan merupakan solusi mewujudkan birokrasi bebas korupsi</h2>
          </div>
        </div>
        <div class="row">
          <div class="col">
            <p>Cari tahu siapa calon presiden dan wakil presiden yang mewakili aspirasimu melalui pertanyaan-pertanyaan yang kami sediakan</p>
            <br/>
            <br/>
          </div>
        </div>
        <div class="row">
          <div class="col">
          </div>
          <div class="col">
            <Link href={{ pathname: 'question', query: { q: 1 }}} as="/question/1">
              <button type="button" class="btn btn-danger btn-block"><b>MULAI</b></button>
            </Link>
          </div>
          <div class="col">
          </div>
        </div>          
      </div>

    )
  }

  render() {
      if (isNaN(this.state.q)) {
          return (this.failure())
      }
      return(this.success())
  }
}
