                onClick={() => this.prevQuestion()}>
                <i className="fas fa-arrow-circle-left"/>
              </button>
            }
          </div>
          <div className="col">
          </div>
          <div className="col text-right">
            { this.hasNextQuestion() &&
              <button
                type="button"
                className="btn btn-danger"
                onClick={() => this.nextQuestion()}>
                <i className="fas fa-arrow-circle-right"/>
              </button>
            }
          </div>
        </div>
      </div>
    )
  }

  render() {
      return(this.success())
  }
}
